tinymce.PluginManager.add('insertcode', function(editor, url) {
	editor.addButton('insertcode', {
        text: 'Code',
        tooltip: 'insert code',
        icon: false,
        onclick: function() {
        	
        	var url = "/code/insert/";
        	
        	editor.windowManager.open({
        	    title: "insert code",
        	    url: url,
        	    width: 700,
        	    height: 210,
        	});
        },
        
    });
});